mapper.insert:
/* BoardDAO.write 글 작성 */
SELECT IFNULL(MAX(G_NO), 0) + 1 FROM tb_board
INSERT INTO tb_board ( G_NO , STEP , INDENT , WRITER , TITLE , CONTENT , REG_DATE ) VALUE ( #{gNo} , 0 , 0 , #{writer} , #{title} , #{content} , NOW() )

/*
BoardDAO.writeReply
게시판 답글 등록
*/

INSERT INTO tb_board (
        G_NO
      , STEP
      , INDENT
      , TITLE
      , CONTENT
      , WRITER
      , REG_DATE
) VALUE (
        #{gNo}
      , #{step}+1
      , #{indent}+1
      , #{title}
      , #{content}
      , #{writer}
      , NOW()
    )

mapper.update:
/*
BoardDAO.replyShape
답글 순서 수정 - 게시글 답글 등록시 기존 등록된 답글의 순서를 증가 시킴
*/

UPDATE tb_board
SET STEP = STEP + 1
WHERE G_NO = #{gNo}
  AND STEP > #{step}
