mapper.select:
/* MemberDAO.memberLogin */

SELECT
   MEM_ID memId
 , MEM_PW memPw
FROM tb_member
WHERE MEM_ID = #{memId}
  AND MEM_PW = #{memPw}

mapper.insert:
/* MemberDAO.member*/

INSERT
INTO
    tb_member (
                MEM_ID
              , MEM_PW
              , MEM_NAME
              , PHONE_NUM
              , MEM_EMAIL

) VALUES (
          #{memId}
         , #{memPw}
         , #{memName}
         , #{phoneNum}
         , #{memEmail}
    )
