mapper.select:
/* MemberDAO.memIdCheck */

SELECT COUNT(MEM_ID)
FROM tb_member
WHERE MEM_ID = #{memId}

mapper.insert:
/* MemberDAO.memberJoin*/

INSERT
INTO tb_member ( MEM_ID
               , MEM_PW
               , MEM_NAME
               , PHONE_NUM
               , MEM_EMAIL
               , MEM_REGDATE)
VALUES ( #{memId}
       , #{memPw}
       , #{memName}
       , #{phoneNum}
       , #{memEmail}
       , NOW())

/* MemberDAO.insertLoginInfo */

INSERT
INTO tb_login_log ( MEM_ID
                  , LOGIN_IP)
VALUES ( #{memNo}
       , #{loginIp})
